$(function(){$(".new_message").live("ajax:beforeSend",function(a){}).live("ajax:success",function(a,b,c,d){var e=b.message,f;e.message_id>0&&(f=$('<div class="b-missing__comment sub"/>').append($('<div class="b-missing__comment_header message l-left">').append($('<a class="b-missing__comment_author"/>').attr("href","/user/"+e.user.id).text(e.user.username)).append($('<span class="b-missing__comment_date t-small l-right"/>').text(e.date))).append($('<div class="b-missing__comment_text"/>').text(e.text)).appendTo(".b-missing__comment[message_id="+e.message_id+"]"),$(this).hide())}),$(".b-report__message_answer").live("click",function(){var a=$(this).parent().parent(),b=a.attr("message_id");$("#new_message").clone().attr("id","").append($('<input type="hidden" name="message[answer_to]"/>').val(b)).appendTo(a).show(),$(this).hide()})})